import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Pergunta {
  id: number;
  conteudo: string;
  alternativas: Alternativa[];
}

interface Alternativa {
  id: number;
  resposta: string;
}

@Component({
  selector: 'app-tela-perguntas',
  templateUrl: './tela-perguntas.component.html',
  styleUrls: ['./tela-perguntas.component.css']
})
export class TelaPerguntasComponent implements OnInit {
  question: Pergunta | undefined;
  selectedAnswer: number | undefined;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.loadQuestion();
  }

  loadQuestion() {
    // Chamada HTTP para carregar a próxima pergunta do backend
    this.http.get<Pergunta>('URL_DA_API_PARA_CARREGAR_PERGUNTA').subscribe(data => {
      this.question = data;
    });
  }

  selectAnswer(alternativaId: number) {
    this.selectedAnswer = alternativaId;
  }

  nextQuestion() {
    if (this.selectedAnswer !== undefined) {
      // Enviar resposta selecionada para o backend
      this.http.post('URL_DA_API_PARA_ENVIAR_RESPOSTA', { perguntaId: this.question?.id, respostaId: this.selectedAnswer }).subscribe(() => {
        // Limpar a resposta selecionada
        this.selectedAnswer = undefined;
        // Carregar a próxima pergunta
        this.loadQuestion();
      });
    }
  }
}
