import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Pergunta {
  id: number;
  conteudo: string;
  alternativas: Alternativa[];
}

interface Alternativa {
  id: number;
  resposta: string;
}

@Component({
  selector: 'app-quiz-question',
  templateUrl: './quiz-question.component.html',
  styleUrls: ['./quiz-question.component.css']
})
export class QuizQuestionComponent implements OnInit {
  question: Pergunta;
  selectedAnswer: number;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.loadQuestion();
  }

  loadQuestion() {
    // Aqui você deve fazer uma chamada HTTP para carregar a próxima pergunta do backend
    // Substitua a URL pela rota adequada em seu backend
    this.http.get<Pergunta>('URL_DA_API_PARA_CARREGAR_PERGUNTA').subscribe(data => {
      this.question = data;
    });
  }

  selectAnswer(alternativaId: number) {
    this.selectedAnswer = alternativaId;
  }

  nextQuestion() {
    // Aqui você deve enviar a resposta selecionada para o backend e avançar para a próxima pergunta
    // Substitua a URL pela rota adequada em seu backend
    this.http.post('URL_DA_API_PARA_ENVIAR_RESPOSTA', { perguntaId: this.question.id, respostaId: this.selectedAnswer }).subscribe(() => {
      this.loadQuestion(); // Carrega a próxima pergunta após enviar a resposta
    });
  }
}
